local DripESP = {} local connections = {} local all_settings = {} local player = game:GetService("Players").LocalPlayer local ESPFolder = workspace:FindFirstChild("ESP_Objects") if not ESPFolder then ESPFolder = Instance.new("Folder") ESPFolder.Name = "ESP_Objects" ESPFolder.Parent = workspace end local function updateESPElement(billboard, highlight, targetPart, settings) if not billboard or not billboard.Parent then return end local rootPart = player.Character and player.Character:FindFirstChild("HumanoidRootPart") if not rootPart then return end local distance = (targetPart.Position - rootPart.Position).Magnitude if distance > settings.MaxDistance or not targetPart.Parent then billboard.Enabled = false if highlight then highlight.Enabled = false end return false end billboard.Enabled = true if highlight then highlight.Enabled = true end return true end function applyESP(target, ESP_ID, settings) if not target or not target.Parent then return end local isValidType = (settings.TargetType == "Both") or (settings.TargetType == "Model" and target:IsA("Model")) or (settings.TargetType == "Part" and target:IsA("BasePart")) if not isValidType or target.Name ~= settings.TargetName then return end if target:IsA("Model") and settings.CheckForHumanoid and not target:FindFirstChild("Humanoid") then return end local targetPart = target:IsA("Model") and (target.PrimaryPart or target:FindFirstChildWhichIsA("BasePart")) or target if not targetPart then return end local uniqueId = ESP_ID .. "_" .. target:GetDebugId() local billboard = ESPFolder:FindFirstChild(uniqueId .. "_BB") local highlight = ESPFolder:FindFirstChild(uniqueId .. "_HL") if not billboard then local displayText = settings.CustomText or "Target" if settings.CustomTextFormatter then local customText = settings.CustomTextFormatter(target) if customText then displayText = customText elseif customText == nil then if ESP_ID == "NormalDoorESP" then return end end end billboard = Instance.new("BillboardGui") billboard.Name = uniqueId .. "_BB" billboard.Adornee = targetPart billboard.Size = UDim2.new(0, 120, 0, 30) billboard.StudsOffset = Vector3.new(0, 2, 0) billboard.AlwaysOnTop = true billboard.Enabled = false billboard.Parent = ESPFolder local label = Instance.new("TextLabel") label.Name = "ESP_Text" label.Parent = billboard label.Size = UDim2.new(1, 0, 1, 0) label.BackgroundTransparency = 1 label.TextColor3 = settings.TextColor label.TextStrokeColor3 = Color3.new(0, 0, 0) label.TextStrokeTransparency = 0.3 label.TextSize = settings.TextSize label.Font = Enum.Font.GothamBold label.Text = displayText end if not highlight and settings.FillTransparency < 1 then highlight = Instance.new("Highlight") highlight.Name = uniqueId .. "_HL" highlight.Adornee = target highlight.FillColor = settings.FillColor highlight.FillTransparency = settings.FillTransparency highlight.OutlineColor = settings.OutlineColor highlight.OutlineTransparency = settings.OutlineTransparency highlight.DepthMode = Enum.HighlightDepthMode.AlwaysOnTop highlight.Enabled = false highlight.Parent = ESPFolder end updateESPElement(billboard, highlight, targetPart, settings) end function DripESP.SetOptions(ESP_ID, opts) all_settings[ESP_ID] = { TargetName = opts.TargetName or "Model", CustomText = opts.CustomText or "Target", TextColor = opts.TextColor or Color3.fromRGB(255, 255, 255), OutlineColor = opts.OutlineColor or Color3.fromRGB(0, 0, 0), FillColor = opts.FillColor or Color3.fromRGB(255, 0, 0), FillTransparency = opts.FillTransparency or 0.7, OutlineTransparency = opts.OutlineTransparency or 0, TextSize = opts.TextSize or 16, CheckForHumanoid = opts.CheckForHumanoid or false, TargetType = opts.TargetType or "Both", MaxDistance = opts.MaxDistance or 1000, CustomTextFormatter = opts.CustomTextFormatter or nil, IsEnabled = false, } end function DripESP.Enable(ESP_ID) local settings = all_settings[ESP_ID] if not settings or settings.IsEnabled then return end settings.IsEnabled = true connections[ESP_ID] = workspace.DescendantAdded:Connect(function(v) if settings.IsEnabled and v.Name == settings.TargetName then task.wait(0.1) applyESP(v, ESP_ID, settings) end end) end function DripESP.Disable(ESP_ID) local settings = all_settings[ESP_ID] if not settings or not settings.IsEnabled then return end settings.IsEnabled = false if connections[ESP_ID] then connections[ESP_ID]:Disconnect() connections[ESP_ID] = nil end end local function GlobalESPUpdater() while true do local rootPart = player.Character and player.Character:FindFirstChild("HumanoidRootPart") if rootPart then local espChildren = ESPFolder:GetChildren() for i = #espChildren, 1, -1 do local item = espChildren[i] if item and item.Parent then local adornee = item.Adornee if adornee and adornee.Parent then local espId = item.Name:match("^(.-)_") local settings = all_settings[espId] if settings and settings.IsEnabled then local highlightName = espId .. "_" .. adornee:GetDebugId() .. "_HL" local highlight = ESPFolder:FindFirstChild(highlightName) updateESPElement(item, highlight, adornee, settings) else item.Enabled = false local highlight = ESPFolder:FindFirstChild(item.Name:gsub("_BB$", "_HL")) if highlight then highlight.Enabled = false end end else item:Destroy() end end end end task.wait(0.2) end end task.spawn(GlobalESPUpdater) return DripESP